import React, { useState, useEffect, useRef } from 'react';
import { useTasks } from '../contexts/TaskContext';
import { PlayIcon, PauseIcon, ResetIcon } from '@radix-ui/react-icons';

const PomodoroTimer = () => {
  const { logSession } = useTasks();
  const [mode, setMode] = useState('focus'); // 'focus' or 'break'
  const [isRunning, setIsRunning] = useState(false);
  const [timeLeft, setTimeLeft] = useState(25 * 60); // 25 minutes in seconds
  const [focusDuration, setFocusDuration] = useState(25);
  const [breakDuration, setBreakDuration] = useState(5);
  const [cycles, setCycles] = useState(0);
  const audioRef = useRef(null);

  useEffect(() => {
    setTimeLeft(mode === 'focus' ? focusDuration * 60 : breakDuration * 60);
  }, [mode, focusDuration, breakDuration]);

  useEffect(() => {
    let interval;
    if (isRunning && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft(prev => prev - 1);
      }, 1000);
    } else if (isRunning && timeLeft === 0) {
      // Timer completed
      audioRef.current.play();
      const sessionType = mode;
      const sessionDuration = mode === 'focus' ? focusDuration : breakDuration;
      
      logSession({
        type: sessionType,
        duration: sessionDuration
      });

      // Switch mode
      setMode(prev => prev === 'focus' ? 'break' : 'focus');
      setTimeLeft(mode === 'focus' ? breakDuration * 60 : focusDuration * 60);
      
      if (mode === 'focus') {
        setCycles(prev => prev + 1);
      }
    }
    return () => clearInterval(interval);
  }, [isRunning, timeLeft, mode, focusDuration, breakDuration, logSession]);

  const toggleTimer = () => {
    setIsRunning(!isRunning);
  };

  const resetTimer = () => {
    setIsRunning(false);
    setTimeLeft(mode === 'focus' ? focusDuration * 60 : breakDuration * 60);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const progress = (timeLeft / (mode === 'focus' ? focusDuration * 60 : breakDuration * 60)) * 100;

  return (
    <div className="card bg-base-200 shadow-md mb-4">
      <div className="card-body p-4">
        <h2 className="card-title justify-center mb-4">
          {mode === 'focus' ? 'Focus Time' : 'Break Time'}
        </h2>
        
        <div className="flex flex-col items-center">
          <div className="relative w-48 h-48 mb-6">
            <svg className="w-full h-full" viewBox="0 0 100 100">
              <circle
                className="text-base-300"
                strokeWidth="8"
                stroke="currentColor"
                fill="transparent"
                r="40"
                cx="50"
                cy="50"
              />
              <circle
                className={`${mode === 'focus' ? 'text-primary' : 'text-accent'}`}
                strokeWidth="8"
                strokeLinecap="round"
                stroke="currentColor"
                fill="transparent"
                r="40"
                cx="50"
                cy="50"
                strokeDasharray={`${2 * Math.PI * 40}`}
                strokeDashoffset={`${2 * Math.PI * 40 * (progress / 100)}`}
                transform="rotate(-90 50 50)"
              />
            </svg>
            <div className="absolute inset-0 flex items-center justify-center">
              <span className="text-4xl font-bold">
                {formatTime(timeLeft)}
              </span>
            </div>
          </div>
          
          <div className="flex gap-4 mb-6">
            <button
              onClick={toggleTimer}
              className={`btn btn-lg ${isRunning ? 'btn-error' : 'btn-success'}`}
            >
              {isRunning ? (
                <>
                  <PauseIcon className="w-5 h-5 mr-2" />
                  Pause
                </>
              ) : (
                <>
                  <PlayIcon className="w-5 h-5 mr-2" />
                  Start
                </>
              )}
            </button>
            <button
              onClick={resetTimer}
              className="btn btn-lg btn-ghost"
            >
              <ResetIcon className="w-5 h-5 mr-2" />
              Reset
            </button>
          </div>
        </div>

        <div className="flex justify-between items-center">
          <div>
            <label className="label">
              <span className="label-text">Focus Duration (min)</span>
            </label>
            <input
              type="number"
              min="1"
              max="60"
              value={focusDuration}
              onChange={(e) => setFocusDuration(parseInt(e.target.value) || 25)}
              className="input input-bordered w-20"
              disabled={isRunning}
            />
          </div>
          <div>
            <label className="label">
              <span className="label-text">Break Duration (min)</span>
            </label>
            <input
              type="number"
              min="1"
              max="30"
              value={breakDuration}
              onChange={(e) => setBreakDuration(parseInt(e.target.value) || 5)}
              className="input input-bordered w-20"
              disabled={isRunning}
            />
          </div>
        </div>

        <div className="text-center mt-4">
          <p className="text-sm">
            Completed focus sessions: <span className="font-bold">{cycles}</span>
          </p>
        </div>

        <audio ref={audioRef} src="/notification.mp3" preload="auto" />
      </div>
    </div>
  );
};

export default PomodoroTimer;
