import React, { useState } from 'react';
import { useTasks } from '../contexts/TaskContext';
import { CheckIcon, TrashIcon, Pencil1Icon, ClockIcon } from '@radix-ui/react-icons';
import { Dialog, DialogTrigger, DialogContent, DialogTitle, DialogDescription } from '@radix-ui/react-dialog';
import { Tooltip, TooltipTrigger, TooltipContent } from '@radix-ui/react-tooltip';

const priorityColors = {
  High: 'bg-error text-error-content',
  Medium: 'bg-warning text-warning-content',
  Low: 'bg-success text-success-content'
};

const categoryColors = {
  Work: 'bg-primary text-primary-content',
  Personal: 'bg-secondary text-secondary-content',
  Study: 'bg-accent text-accent-content'
};

const TaskItem = ({ task }) => {
  const { updateTask, deleteTask } = useTasks();
  const [isEditing, setIsEditing] = useState(false);
  const [editedTask, setEditedTask] = useState({ ...task });

  const handleComplete = async () => {
    await updateTask(task.id, { completed: !task.completed });
  };

  const handleDelete = async () => {
    await deleteTask(task.id);
  };

  const handleEdit = async () => {
    await updateTask(task.id, editedTask);
    setIsEditing(false);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'No due date';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };

  return (
    <div className={`card bg-base-200 shadow-md mb-4 ${task.completed ? 'opacity-70' : ''}`}>
      <div className="card-body p-4">
        <div className="flex items-start justify-between">
          <div className="flex items-start gap-3">
            <button
              onClick={handleComplete}
              className={`btn btn-circle btn-sm ${task.completed ? 'btn-success' : 'btn-ghost'}`}
            >
              {task.completed && <CheckIcon className="w-4 h-4" />}
            </button>
            <div className="flex-1">
              <h3 className={`text-lg font-medium ${task.completed ? 'line-through' : ''}`}>
                {task.title}
              </h3>
              {task.description && (
                <p className="text-sm text-gray-500 mt-1">{task.description}</p>
              )}
              <div className="flex gap-2 mt-2">
                <span className={`badge ${priorityColors[task.priority]}`}>
                  {task.priority}
                </span>
                <span className={`badge ${categoryColors[task.category]}`}>
                  {task.category}
                </span>
                {task.dueDate && (
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <span className="badge bg-neutral text-neutral-content">
                        <ClockIcon className="w-3 h-3 mr-1" />
                        {formatDate(task.dueDate)}
                      </span>
                    </TooltipTrigger>
                    <TooltipContent>
                      Due on {new Date(task.dueDate).toLocaleDateString()}
                    </TooltipContent>
                  </Tooltip>
                )}
              </div>
            </div>
          </div>
          <div className="flex gap-2">
            <button
              onClick={() => setIsEditing(true)}
              className="btn btn-ghost btn-sm"
            >
              <Pencil1Icon className="w-4 h-4" />
            </button>
            <button
              onClick={handleDelete}
              className="btn btn-ghost btn-sm text-error"
            >
              <TrashIcon className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>

      {/* Edit Dialog */}
      <Dialog open={isEditing} onOpenChange={setIsEditing}>
        <DialogContent className="bg-base-100 p-6 rounded-lg shadow-xl max-w-md">
          <DialogTitle className="text-xl font-bold mb-4">Edit Task</DialogTitle>
          <div className="space-y-4">
            <div>
              <label className="label">
                <span className="label-text">Title</span>
              </label>
              <input
                type="text"
                value={editedTask.title}
                onChange={(e) => setEditedTask({ ...editedTask, title: e.target.value })}
                className="input input-bordered w-full"
              />
            </div>
            <div>
              <label className="label">
                <span className="label-text">Description</span>
              </label>
              <textarea
                value={editedTask.description || ''}
                onChange={(e) => setEditedTask({ ...editedTask, description: e.target.value })}
                className="textarea textarea-bordered w-full"
                rows="3"
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="label">
                  <span className="label-text">Priority</span>
                </label>
                <select
                  value={editedTask.priority}
                  onChange={(e) => setEditedTask({ ...editedTask, priority: e.target.value })}
                  className="select select-bordered w-full"
                >
                  <option value="High">High</option>
                  <option value="Medium">Medium</option>
                  <option value="Low">Low</option>
                </select>
              </div>
              <div>
                <label className="label">
                  <span className="label-text">Category</span>
                </label>
                <select
                  value={editedTask.category}
                  onChange={(e) => setEditedTask({ ...editedTask, category: e.target.value })}
                  className="select select-bordered w-full"
                >
                  <option value="Work">Work</option>
                  <option value="Personal">Personal</option>
                  <option value="Study">Study</option>
                </select>
              </div>
            </div>
            <div>
              <label className="label">
                <span className="label-text">Due Date</span>
              </label>
              <input
                type="date"
                value={editedTask.dueDate ? editedTask.dueDate.split('T')[0] : ''}
                onChange={(e) => setEditedTask({ ...editedTask, dueDate: e.target.value })}
                className="input input-bordered w-full"
              />
            </div>
            <div className="flex justify-end gap-2 pt-4">
              <button
                onClick={() => setIsEditing(false)}
                className="btn btn-ghost"
              >
                Cancel
              </button>
              <button
                onClick={handleEdit}
                className="btn btn-primary"
              >
                Save Changes
              </button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default TaskItem;
