import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, NavLink } from 'react-router-dom';
import { ThemeProvider } from './contexts/ThemeContext';
import { TaskProvider } from './contexts/TaskContext';
import HomePage from './pages/HomePage';
import StatsPage from './pages/StatsPage';
import { HomeIcon, BarChartIcon } from '@radix-ui/react-icons';
import ThemeToggle from './components/ThemeToggle';
import './App.css';

const App = () => {
  return (
    <ThemeProvider>
      <TaskProvider>
        <Router>
          <div className="min-h-screen flex flex-col">
            {/* Modern Navbar */}
            <nav className="bg-white dark:bg-gray-900 shadow-sm">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex justify-between h-16 items-center">
                  <Link to="/" className="text-xl font-semibold text-gray-900 dark:text-white">
                    Productivity Pro
                  </Link>
                  <div className="flex space-x-4">
                    <NavLink to="/" className="px-3 py-2 rounded-md text-sm font-medium">
                      Tasks
                    </NavLink>
                    <NavLink to="/stats" className="px-3 py-2 rounded-md text-sm font-medium">
                      Analytics
                    </NavLink>
                    <ThemeToggle />
                  </div>
                </div>
              </div>
            </nav>

            {/* Modern Content Container */}
            <main className="flex-grow max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
              <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/stats" element={<StatsPage />} />
              </Routes>
            </main>

            {/* Modern Footer */}
            <footer className="bg-white dark:bg-gray-900 border-t border-gray-200 dark:border-gray-800 py-4">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center text-sm text-gray-500 dark:text-gray-400">
                Â© {new Date().getFullYear()} Productivity Pro
              </div>
            </footer>
          </div>
        </Router>
      </TaskProvider>
    </ThemeProvider>
  );
};

export default App;
